 * bot permission, see https://developer.fusebit.io/docs/discord#creating-your-own-discord-app.
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param channelId {string} Channel Id
 * @param content {string} Message content
 * @returns {object} Discord response
 */
async function discordSendMessageToChannelId(ctx, channelId, content) {
  // For the Discord API documentation, see https://discord.com/developers/docs/reference.
  const discordClient = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );
  return await discordClient.bot.post(`channels/${channelId}/messages`, { content });
}

/**
 * Send a message to a Discord channel identified by name. This function requires a custom Discord app with send message 
 * bot permission, see https://developer.fusebit.io/docs/discord#creating-your-own-discord-app.
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param guildId {string} Guild Id
 * @param channelName {string} Channel name
 * @param content {string} Message content
 * @returns {object} Discord response
 */
async function discordSendMessageToChannelName(ctx, guildId, channelName, content) {
  // For the Discord API documentation, see https://discord.com/developers/docs/reference.
  const discordClient = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );
  const channels = await discordClient.bot.get(`guilds/${guildId}/channels`);
  const channel = channels.find((c) => c.name === channelName);
  if (!channel) {
    ctx.throw(404, `Channel '${channelName}' not found`);
  }
  return await discordClient.bot.post(`channels/${channel.id}/messages`, { content });
}

/**
 * Send a message to the default webhook channel on Discord. This function requires a custom Discord app with send message 
 * bot permission, see https://developer.fusebit.io/docs/discord#creating-your-own-discord-app.
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param content {string} Message content
 * @returns {object} Discord response
 */
async function discordSendMessage(ctx, content) {
  // For the Discord API documentation, see https://discord.com/developers/docs/reference.
  const discordClient = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );
  // By default send message to the webhook channel selected during installation
  const channelId = discordClient?.fusebit?.credentials?.webhook?.channel_id;
  if (!channelId) {
    ctx.throw(404, `The installation does not have a default webhook channel.`);
  }
  return await discordClient.bot.post(`channels/${channelId}/messages`, { content });
}

/**
 * Close an existing Github issue. 
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param owner {string} Repository owner. 
 * @param repo {string} Repository name.
 * @param issueNumber {number} Github Issue number.
 * @returns {object} Closed issue.
 */
async function githubappCloseIssue(ctx, owner, repo, issueNumber) {
  // For the Github API documentation, see https://github.com/octokit/octokit.js.
  const githubapp = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );

  // Ensure you have configured your GitHub Connector properly in order to authenticate as a GitHub Application.
  // Setup properly the Client Secret and App ID from your GitHub app in your Connector configuration.
  // See our developer docs for more information https://developer.fusebit.io/docs/github
  const appClient = await githubapp.app();
  const { data: installations } = await appClient.rest.apps.listInstallations();

  if (!installations.length) {
    ctx.throw(404, 'This application has no installations');
  }

  const installation = installations.find((installation) => installation.account.login === ctx.params.owner);

  if (!installation) {
    ctx.throw(404, `Installation not found for account ${ctx.params.owner}`);
  }

  // Now you have your installation, you can request an access token to the specific installation
  // We perform all that work for you and you get back an authenticated SDK as a GitHub installation.
  const installationClient = await appClient.installation(installation.id);

  const { data } = await installationClient.rest.issues.update({
    owner,
    repo,
    issue_number: issueNumber,
    state: 'closed',
  });
  return data;
}

