/**
 * Sends a message to a Slack channel. 
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param message {string|object} The message (in Slack markdown) to send, or the postMessage payload (advanced).
 * @param {channel} [undefined] Optional Slack channel ID or channel name to send the message to. If not specified, a DM is sent.
 */
async function slackSendMessage(ctx, message, channel) {
  // For the Slack SDK documentation, see https://slack.dev/node-slack-sdk/web-api.
  const slackClient = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );

  if (!channel) {
    // Use the Slack user ID of the authorized user as the channel ID to send a Direct Message
    channel = slackClient.fusebit.credentials.authed_user.id;
  }

  return typeof message === 'object'
    ? slackClient.chat.postMessage({
        channel,
        ...message,
      })
    : slackClient.chat.postMessage({
        channel,
        text: message || 'Hello world from Fusebit!',
      });
}

/**
 * Upload a file to a Slack channel. 
 * 
 * @param ctx {FusebitContext} Fusebit Context of the request
 * @param message {payload} The file upload payload.
 */
async function slackUploadFile(ctx, payload) {
  // For the Slack SDK documentation, see https://slack.dev/node-slack-sdk/web-api.
  const slackClient = await integration.tenant.getSdkByTenant(
    ctx,
    '<% connectorName %>',
    ctx.params.tenantId || '<% defaultTenantId %>'
  );

  if (!payload?.channels) {
    // Use the Slack user ID of the authorized user as the channel ID to send a Direct Message
    payload.channels = slackClient.fusebit.credentials.authed_user.id;
  }

  return slackClient.files.upload(payload);
}

/**
 * Confirm the command is received
 * 
 * @param ctx {FusebitContext} Fusebit Context
 */
router.post('/api/fusebit/webhook/event/immediate-response', (ctx) => {
    const { command } = ctx.req.body;
    ctx.body = {
      text: `:hourglass_flowing_sand: We got the command!, we are running the <@${command}> command.`,
      response_type: 'ephemeral',
    };
  });

/**
 * Respond to a Slash command
 * 
 * @param ctx {FusebitContext} Fusebit Context
 */
 integration.event.on('/:<% connectorName %>/webhook/slash-command/:command', async (ctx) => {
    try {
        const { team_id, user_id, api_app_id: app_id, channel_id } = ctx.req.body.data;

        const installs = await integration.webhook.searchInstalls(ctx, '<% connectorName %>', {
          app_id,
          team_id,
          user_id,
        });

        const slackClient = await integration.service.getSdk(ctx, '<% connectorName %>', installs[0].id);

        await slackClient.chat.postMessage({
          text: 'Command processing finished',
          channel: channel_id,
        });

      } catch (error) {
        // Detect if the error is coming because no Installs were returned
        if (error.statusCode === 404) {
          await integration.webhook.send(ctx, { text: 'Please authorize the application to use commands'});
        } else {
          // Something else failed, log the error and inform the user
          console.log(error.message);
          await integration.webhook.send(ctx, { text: 'Something went wrong!' });
        }
      }
 });

/**
 * Respond to messages in channels that the bot has access to
 * 
 * @param ctx {FusebitContext} Fusebit Context
 */

 integration.event.on('/:componentName/webhook/event_callback', async (ctx) => {
   const slackClient = await integration.service.getSdk(ctx, ctx.params.componentName, ctx.req.body.installIds[0]);

   const messagingUser = ctx.req.body.data.event.user;
   const authorizedListeningUser = ctx.req.body.data.authorizations[0].user_id;

   if (messagingUser === authorizedListeningUser) {
     console.log('Skipping to avoid recursive response (i.e., infinite loop).');
     return;
   }

   const text = ctx.req.body.data.event.text;
   await slackClient.chat.postMessage({
     text: `I'm responding via a webhook.  I was alerted when <@${messagingUser}> sent the message: 

 "${text}"`,
     channel: ctx.req.body.data.event.channel,
   });
 });

